import React from "react";
import styled from "styled-components";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import img1 from "./개발자.jpeg";

const Container = styled.div`
    margin-top: 100px;

    :not(:last-child) {
        margin-bottom: 50px;
    }
`;

const Title = styled.span`
    font-size: 20px;
    font-weight: 600;
`;

const Wrap = styled.div`
    border: 1px solid red;
`;

const Home = () => {
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidedToScroll: 1,
    };
    return (
        <Container>
            <Title>타이틀</Title>
            <Wrap>
                <Slider {...settings}>
                    <div>
                        <h3>
                            <img src={img1}></img>
                        </h3>
                    </div>
                    <div>
                        <h3>2</h3>
                    </div>
                    <div>
                        <h3>3</h3>
                    </div>
                    <div>
                        <h3>4</h3>
                    </div>
                    <div>
                        <h3>5</h3>
                    </div>
                    <div>
                        <h3>6</h3>
                    </div>
                </Slider>
            </Wrap>
        </Container>
    );
};

export default Home;
