import { createActions, handleActions } from "redux-actions";

const CHECKED = "CHECKED";
const INSERT = "INSERT";
const REMOVE = "REMOVE";

export const checked = createActions(CHECKED, (id) => id);

let id = 0;
export const insert = createActions(INSERT, (text) => ({
    id: id++,
    text,
    checked: false,
}));
export const remove = createActions(REMOVE, (id) => id);

const initalState = {
    todolist: [
        {
            id: 1,
            text: "1등하기",
            checked: false,
        },
        {
            id: 2,
            text: "2등하기",
            checked: false,
        },
        {
            id: 3,
            text: "3등하기",
            checked: false,
        },
    ],
};

const todo = handleActions({
    [INSERT]: (state, action) => ({
        ...state,
        todolist: state.todolist.concat(action.payload),
    }),
});

export default todo;
