import React from "react";
import styled from "styled-components";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import img1 from "./개발자.jpeg";

const Container = styled.div`
    height: 300px;
    width: 700px;
    margin: auto;
    margin-top: 100px;
`;

const Title = styled.span`
    font-size: 20px;
    font-weight: 600;
`;

const Wrap = styled.div`
    border: 1px solid red;
`;
const H3 = styled.h3`
    height: 200px;
`;
const Img = styled.img`
    height: 200px;
`;

const Home = () => {
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidedToScroll: 1,
    };

    return (
        <Container>
            <Title>타이틀</Title>
            <Wrap>
                <Slider {...settings}>
                    <div>
                        <H3>
                            <Img src={img1}></Img>
                        </H3>
                    </div>
                    <div>
                        <H3>2</H3>
                    </div>
                    <div>
                        <H3>3</H3>
                    </div>
                    <div>
                        <H3>4</H3>
                    </div>
                    <div>
                        <H3>5</H3>
                    </div>
                    <div>
                        <H3>6</H3>
                    </div>
                </Slider>
            </Wrap>
        </Container>
    );
};

export default Home;
