import React from "react";
import styled from "styled-components";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";

const Container = styled.div`
    margin: auto;
    margin-top: 100px;
    width: 1200px;
`;

const Title = styled.span`
    font-size: 20px;
    font-weight: 600;
`;

const Wrap = styled.div``;
const H3 = styled.h3`
    height: 300px;
    background-color: #78909c;
    margin: auto;
    text-align: center;
    line-height: 250px;
`;

const Home = () => {
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidedToScroll: 1,
    };

    return (
        <Container>
            <Wrap>
                <Slider {...settings}>
                    <div>
                        <H3>1</H3>
                    </div>
                    <div>
                        <H3>2</H3>
                    </div>
                    <div>
                        <H3>3</H3>
                    </div>
                    <div>
                        <H3>4</H3>
                    </div>
                    <div>
                        <H3>5</H3>
                    </div>
                    <div>
                        <H3>6</H3>
                    </div>
                </Slider>
            </Wrap>
        </Container>
    );
};

export default Home;
